using UnityEngine; public class Block : MonoBehaviour { public float breakTime = 0.6f; float timer = 0f; Camera cam; bool isBreaking = false; void Start() { cam = Camera.main; } void Update() { if (!Input.GetMouseButton(0)) { timer = 0f; isBreaking = false; return; } Ray ray = new Ray(cam.transform.position, cam.transform.forward); RaycastHit hit; if (Physics.Raycast(ray, out hit, 5f)) { if (hit.collider.gameObject == gameObject) { isBreaking = true; timer += Time.deltaTime; if (timer >= breakTime) { Destroy(gameObject); } } else { timer = 0f; isBreaking = false; } } else { timer = 0f; isBreaking = false; } } }